// Package must be downloaded from github and copied into the project directory
use PHPMailer\PHPMailer\PHPMailer;
use PHPMailer\PHPMailer\SMTP;
use PHPMailer\PHPMailer\Exception;

// These file paths must be changed to those of the PHPMailer folder added to the project directory
require_once __DIR__ . '/PHPMailer/src/Exception.php';
require_once __DIR__ . '/PHPMailer/src/PHPMailer.php';
require_once __DIR__ . '/PHPMailer/src/SMTP.php';

// passing true in constructor enables exceptions in PHPMailer
$mail = new PHPMailer(true);

try {
    // Server settings
    $mail->SMTPDebug = SMTP::DEBUG_SERVER;
    $mail->isSMTP();
    $mail->Host = 'smtp.gmail.com';
    $mail->SMTPAuth = true;
    $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;
    $mail->Port = 587;

    $mail->Username = 'simpliegreen@gmail.com'; // Gmail account of Sender
    $mail->Password = 'pzuuozowpitgvjad';       // This is the APP password generated through Google account settings

    // Sender and recipient settings
    $mail->setFrom('simpliegreen@gmail.com', 'Touch Tap LLC');
    $mail->addAddress('bromero2@my.hpu.edu', 'Receiver Name');
    $mail->addReplyTo('simpliegreen@gmail.com', 'Touch Tap LLC');

    // Setting the email content
    $mail->IsHTML(true);
    $mail->Subject = "Order Request";
    $mail->Body = 'HTML message body. <b>Gmail</b> SMTP email body.';
    $mail->AltBody = 'Message goes here';


    // $file is the base64 data containing the encoded image
    $file = '';
    // If choosing to add an attachment, file path goes here
    //$file_to_attach = 'C:\Users\simpl\Desktop\Engineering Project\Machine Learning\FirePictures\v2\05DA5060-A737-4364-82D98E916CD1BD34_source.jpg';

    // We must first decode the base64 data inorder to send it as a string.
    // Also, the 'filename' extension must be that of the type being sent (see below .jpg)
    $mail->addStringAttachment(base64_decode($file), 'picture.jpg');
    // If choosing to add an attachment via its file path
    //$mail->AddAttachment( $file_to_attach , 'picture.jpg' );

    $mail->send();
    echo "Email message sent.";
} catch (Exception $e) {
    echo "Error in sending email. Mailer Error: {$mail->ErrorInfo}";
}
